    private void btnSiguienteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSiguienteActionPerformed
        // TODO add your handling code here:
        if(txtNombre.getText().compareTo("")==0)
        {
            JOptionPane.showMessageDialog(getContentPane(),"Debe ingresar el nombre","Error",JOptionPane.ERROR_MESSAGE);
        }
        else
        {
            if(muebleActual==null)
            {
                muebleActual=new MuebleEstructura();
            }
            muebleActual.setNombre(txtNombre.getText());
            PantallaPiezas pantP=new PantallaPiezas();
            pantP.setMuebleActual(muebleActual);
            pantP.setVisible(true);
            this.dispose();
        }
    }//GEN-LAST:event_btnSiguienteActionPerformed

    private void btnCancelarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCancelarActionPerformed
        // TODO add your handling code here:
        PantallaInicialMueble pantCM=new PantallaInicialMueble();
        pantCM.setVisible(true);
        this.dispose();
    }//GEN-LAST:event_btnCancelarActionPerformed

